package week1.day2;

public class TwoWheeler {
	int noOfWheels = 2;
	short noOfMirrors = 2;
	long chassisNumber = 9876543210L;
	boolean isPunctured = false;
	String bikeName = "RoyalEnfield";
	double runningKM = 9800.5;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		TwoWheeler re = new TwoWheeler();
		System.out.println(re.noOfWheels);
		System.out.println(re.noOfMirrors);
		System.out.println(re.chassisNumber);
		System.out.println(re.isPunctured);
		System.out.println(re.bikeName);
		System.out.println(re.runningKM);		

	}

}
